pragma solidity 0.8.26;

import { ISchemaResolver } from "@ethereum-attestation-service/eas-contracts/contracts/resolver/ISchemaResolver.sol";

struct UserCollateral {
  uint256 lockedCollateral;
  uint256 freeCollateral;
}

interface ITCR is ISchemaResolver {
  function setBuyCollateralResolver(
   address buyCollateralResolver 
  ) external;

  function setSellCollateralResolver(
   address sellCollateralResolver 
  ) external;

  function addCollateral(
    address validator,
    address collateralToken,
    uint256 collateral
  ) external payable;

  function userCollateral(
    address user,
    address paymentToken
  ) external returns (UserCollateral memory);
}

pragma solidity 0.8.26;

import "hardhat/console.sol";

import { IEAS, Attestation } from "@ethereum-attestation-service/eas-contracts/contracts/IEAS.sol";
import { SchemaResolver } from "@ethereum-attestation-service/eas-contracts/contracts/resolver/SchemaResolver.sol";
import { ISchemaResolver } from "@ethereum-attestation-service/eas-contracts/contracts/resolver/ISchemaResolver.sol";
import { UserCollateral } from "./ITCR.sol";

import { IERC20  } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20  } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract TrustedValidatorResolver is SchemaResolver {
  using SafeERC20 for IERC20;
  error InvalidAllowance();

  address validator; 
  address buyCollateralResolver;
  address sellCollateralResolver;

  // user => erc20 address => collateral struct
  mapping (address => mapping (address => UserCollateral)) public userCollateral;

  constructor (
    IEAS eas,
    address _validator
  ) SchemaResolver(eas) {
    validator = _validator;
  }

  modifier onlyResolvers() {
    require(msg.sender == buyCollateralResolver || msg.sender == sellCollateralResolver, "Only resolvers");
    _;
  }

  modifier onlyValidator() {
    require(msg.sender == validator, "Only validator");
    _;
  }

  function setBuyCollateralResolver(
    address _buyCollateralResolver
  ) public onlyValidator {
    buyCollateralResolver = _buyCollateralResolver; 
  }

  function setSellCollateralResolver(
    address _sellCollateralResolver 
  ) public onlyValidator {
    sellCollateralResolver = _sellCollateralResolver;
  }


  function onAttest(
    Attestation calldata attestation,
    uint256 value
  ) internal override returns (bool) {
    (
      bool isApproved
    ) = abi.decode(
      attestation.data,
      (bool)
    );

    Attestation memory sellerAttestation = _eas.getAttestation(
      attestation.refUID
    );
    (
      uint256 collateral
    ) = abi.decode(
      sellerAttestation.data,
      (uint256)
    );
    Attestation memory buyerAttestation = _eas.getAttestation(
      sellerAttestation.refUID
    );

    ( /*address supplier*/,
      uint256 jobCost,
      address paymentToken,
      /*uint256 creditsRequested*/,
      /*uint256 collateralRequested*/,
      /*uint256 offerDeadline*/,
      /*uint256 jobDeadline*/,
      uint256 arbitrationDeadline
    ) = abi.decode(
    buyerAttestation.data,
    (address, uint256, address, uint256, uint256, uint256, uint256, uint256)
    );

    if (isApproved) {
      // Reward and Collateral Refund
      IERC20(paymentToken).transfer(
        sellerAttestation.recipient,
        jobCost + collateral
      );
    } else {
      // Refund and get collateral
      IERC20(paymentToken).transfer(
        buyerAttestation.recipient,
        jobCost + collateral
      );
    }

    return true;
  }

  function onRevoke(
    Attestation calldata attestation,
    uint256 value
  ) internal pure override returns (bool) {

  }


  function addCollateral(
    address user,
    address paymentToken,
    uint256 collateral
  ) public payable onlyResolvers {
    userCollateral[user][paymentToken].lockedCollateral += collateral;
  }
}

contracts/EasUtil.sol.content:
pragma solidity 0.8.26;

import {
  IEAS,
  AttestationRequest,
  AttestationRequestData
} from "@ethereum-attestation-service/eas-contracts/contracts/IEAS.sol";
import { ISchemaRegistry } from "@ethereum-attestation-service/eas-contracts/contracts/ISchemaRegistry.sol";
import { ISchemaResolver } from "@ethereum-attestation-service/eas-contracts/contracts/resolver/ISchemaResolver.sol";

library EasUtil {

  function getEAS() public view returns (IEAS) {
    return IEAS(0xA1207F3BBa224E2c9c3c6D5aF63D0eb1582Ce587);
  }


  function getSchemaRegistry() public view returns (ISchemaRegistry) {
    return ISchemaRegistry(0xA7b39296258348C78294F95B872b282326A97BDF);
  }

  function attest(
    bytes32 schemaUid,
    address recipient,
    bytes32 refUID,
    bytes memory data,
    uint256 value
  ) public returns (bytes32 attestUID) {

    AttestationRequestData memory requestData = AttestationRequestData({
      recipient: recipient,
      expirationTime: 0,
      revocable: true,
      refUID: refUID,
      data: data,
      value: value
    });
    AttestationRequest memory request = AttestationRequest({
      schema: schemaUid,
      data: requestData
    });
    return getEAS().attest{value:value}(request);
  }

  function register(
    string calldata schema ,
    ISchemaResolver resolver,
    bool revocable
  ) public returns (bytes32 schemaUID) {
    return getSchemaRegistry().register(schema, resolver, revocable);
  }

  function prepareDCNSchemas(
    address deployer,
    ISchemaResolver  buyCollateralResolver,
    ISchemaResolver  sellCollateralResolver,
    ISchemaResolver  validatorResolver
  ) public returns (
    bytes32 buySchemaUID,
    bytes32 sellSchemaUID,
    bytes32 validatorSchemaUID
  ) {
    string memory buySchema = "uint256 amount, uint256 collateralRequested, address validator, uint256 deadline";
    string memory sellSchema = "uint256 collateral, address validator";
    string memory validatorSchema = "bool isApproved";

    buySchemaUID = getSchemaRegistry().register(
      buySchema, buyCollateralResolver, true
    );
    sellSchemaUID = getSchemaRegistry().register(
      sellSchema, sellCollateralResolver, true
    );
    validatorSchemaUID = getSchemaRegistry().register(
      validatorSchema, validatorResolver, true
    );
  }



}

